<script>
import { ref, watch } from 'vue';

export default {
  props: {
    browserType: {
      type: String,
      default: 'all',
    },
    browserMode: {
      type: String,
      default: 'grid',
    },
    currentNodeId: {
      type: String,
      default: '0',
    },
  },
  emits: ['change-browser-type', 'reset-folder', 'fold-all', 'unfold-all'],
  setup(props, context) {
    const breadcrumb = ref([]);

    let nodesArr = [];
    const getBreadcrumb = async (nodeId) => {
      const result = await chrome.bookmarks.get(nodeId);
      const node = result[0];
      nodesArr.unshift(node);
      if (node.parentId) {
        await getBreadcrumb(node.parentId);
      }
    };

    watch(
      () => props.currentNodeId,
      async (newValue, oldValue) => {
        console.log(props.currentNodeId);
        await getBreadcrumb(props.currentNodeId);
        breadcrumb.value = nodesArr;
        nodesArr = [];
        console.log('breadcrumb', breadcrumb.value);
      },
      {
        immediate: true,
      },
    );

    return {
      breadcrumb,
    };
  },
};
</script>

