<script>
import { ref } from 'vue';

export default {
  props: {
    bookmarkOpenMode: {
      type: String,
      default: 'all',
    },
    multiOnGroup: Boolean,
    groupName: {
      type: String,
      default: '',
    },
    groupColor: {
      type: String,
      default: '#1A73E8',
    },
    pinNodesId: {
      type: Array,
      default() {
        return [];
      },
    },
  },
  emits: ['change-browser-type', 'update:multiOnGroup', 'update:groupName', 'set-group-color', 'fold-all', 'unfold-all'],
  setup(props, context) {
    const showColorPalette = ref(false);
    // group color
    const colorScheme = [
      {
        name: 'grey',
        value: '#5F6368',
      },
      {
        name: 'blue',
        value: '#1A73E8',
      },
      {
        name: 'red',
        value: '#D93025',
      },
      {
        name: 'yellow',
        value: '#E37400',
      },
      {
        name: 'green',
        value: '#188038',
      },
      {
        name: 'pink',
        value: '#D01884',
      },
      {
        name: 'purple',
        value: '#9334E6',
      },
      {
        name: 'cyan',
        value: '#007B83',
      },
    ];

    function setGroupColorHandler(value) {
      context.emit('set-group-color', value);
      showColorPalette.value = false;
    }

    return {
      showColorPalette,
      colorScheme,
      setGroupColorHandler,
    };
  },
};
</script>

